<style>
    section {
        position: relative;
        height: 500px;
        margin: 50px 0;
        padding-top: 20px;
    }

    section:hover .overlay {
        transform: translate(150px);
        clip-path: polygon(64% 0, 100% 0, 90% 100%, 41% 100%);
    }
    section:hover .overlay h1 {
        transform: translate(270px);
    }
    .overlay-xs {
        display: none;
    }
    .container {
        margin-top: 50px;
        position: absolute;
        height: 400px;
        width: 100%;
        background: white;
        clip-path: polygon(3% 0%, 100% 0%, 83% 100%, 0% 100%);
    }
    .overlay {
        position: absolute;
        height: 475px;
        width: 100%;
        clip-path: polygon(3% 0, 66% 0, 100% 100%, 5% 100%);
        background: rgb(16, 55, 132);
        display: flex;
        justify-content: center;
        transition: all 0.8s ease-in-out;
    }
    .right-shadow {
        filter: drop-shadow(12px 4px 8px rgba(0, 0, 0, 0.3));
        z-index: 80;
    }

    .left-shadow {
        position: relative;
        z-index: 99;
        filter: drop-shadow(12px 6px 6px rgba(0, 0, 0, 0.5));
    }
    h1 {
        font-family: "Ubuntu", sans-serif;
        padding-top: 30px;
        font-size: 5rem;
        color: #fff;
    }
    .overlay h1 {
        transform: translateX(-150px);
        transition: all 0.8s ease-in;
    }
    svg {
        height: 10rem;
        width: 10rem;
        padding: 15px;
    }
    .icon-grid {
        padding-left: 30px;
        display: flex;
        width: 70%;
        flex-wrap: wrap;
    }
    @media (max-width: 1200px) {
        section {
            width: 90%;
        }
    }
    @media (max-width: 1199px) {
        svg {
            height: 9rem;
            width: 9rem;
            padding: 10px;
        }
    }
    @media (max-width: 1130px) {
        svg {
            height: 9rem;
            width: 9rem;
            padding: 10px;
        }
        section:hover .overlay {
            transform: translate(100px);
            clip-path: polygon(64% 0, 100% 0, 90% 100%, 41% 100%);
        }

        section:hover .overlay h1 {
            transform: translate(250px);
        }
    }
    @media (max-width: 1024px) {
        .container {
            width: 90%;
        }
        .overlay {
            width: 90%;
            transform: translate(120px);
            clip-path: polygon(64% 0, 100% 0, 90% 100%, 41% 100%);
        }

        .overlay h1 {
            font-size: 3.5rem;
            transform: translate(210px);
        }
        svg {
            height: 8rem;
            width: 8rem;
            padding: 10px;
        }
        section:hover .overlay {
            transform: translate(120px);
        }
        section:hover .overlay h1 {
            transform: translate(210px);
        }
    }

    @media (max-width: 835px) {
        section {
            margin: 0 auto;
            height: 400px;
            padding: 0;
            margin-bottom: 100px;
        }
        .overlay {
            display: none;

            height: 100px;

            clip-path: polygon(0% 0, 100% 0, 100% 100%, 0% 100%);
        }

        .overlay-xs {
            z-index: -1;
            margin: 0 auto;
            width: 60%;
            display: block;
            background: rgb(16, 55, 132);
        }
        .container {
            margin: 50px auto;
            width: 100%;
            height: auto;
            text-align: center;
            clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
        }
        h1 {
            transform: translate(0px);
            text-align: center;
            padding: 10px 10px;
            font-size: 4rem;
        }
        .icon-grid {
            margin: 0 auto;
            display: flex;
            justify-content: space-evenly;
            width: 100%;
            height: 100%;
            padding: 0;
            padding-top: 20px;
        }
        svg {
            width: 10rem;
            height: 10rem;
            padding: 15px;
        }
    }
    @media (max-width: 737px) {
        section {
            height: 600px;
            margin-bottom: 50px;
        }
    }
    @media (max-width: 737px) {
        section {
            height: 750px;
        }
    }
    @media (max-width: 500px) {
        h1 {
            font-size: 3rem;
        }
    }
    @media (max-width: 448px) {
        section {
            height: 1000px;
        }
    }
</style>

